    }
`


const Hero = ({ slides }) => {
    return (
        <HeroSection>
            <HeroWrapper>
            {slides.map((slide,index)=> {
                return (
                    <HeroSlide key={index}>
                        <HeroSlider>
                            <HeroImage />
                            <HeroContent>
                                <h1>{slide.title}</h1>
                                <p>{slide.price}</p>
                                <Button to={slide.path} primary="true" css={`max-width: 160px`}>
                                    {slide.label}
                                    <Arrow />
                                </Button>
                            </HeroContent>
                        </HeroSlider>
                    </HeroSlide>
                )
            })}
                <SliderButton>
                    <PrevArrow />
                    <NextArrow />
                </SliderButton>
            </HeroWrapper>
        </HeroSection>
    )
}

export default Hero
